INSERT INTO `rallys` (`id`, `title`, `slug`, `short_description`, `full_description`, `logo_src`) VALUES
(1, 'ชุมนุม ITForge EROS', 'itforge', 'IT นั้นย่อมาจาก Information Technology แปลว่า เทคโนโลยีสารสนเทศ ส่วน Forge นั้นแปลว่า การตีเหล็ก หรือ โรงตีเหล็ก เพราะฉะนั้นเมื่อนำสองคำนี้มารวมกันจะหมายถึง การตีความรู้ทางด้านเทคโนโลยีสารสนเทศให้กล้าแข็งขึ้น หรือ หมายถึงสถานที่สำหรับการพัฒนาความรู้ทางด้านเทคโนโลยี', '<p>ชุมนุมของเราเป็นชุมนุมที่จะช่วยส่งเสริมวิชาการทางด้านเทคโนโลยีสารสนเทศ ทั้งที่มีสอนในและนอกคณะ โดยเป็นการสอนจากรุ่นพี่ไปสู่รุ่นน้อง นอกจากนี้ชุมนุมของพวกเรานั้นยังช่วยส่งเสริมและเตรียมความพร้อมในการแข่งขันวิชาการของนักศึกษาที่จัดขึ้นในทุกๆปี เป็นสถานที่สำหรับการพัฒนาความรู้ทางด้านเทคโนโลยีของน้องๆ ที่สนใจได้มาเรียนรู้แน่นอน</p>\r\n', 'rally_it_forge.png'),
(2, 'ชุมนุมวิจัยอนิเมและหมากกระดาน', 'anime', 'สมัยนี้ อนิเมชั่นหรือการ์ตูนต่างๆเป็นสื่อที่อยู่ใกล้ตัว และเป็นที่รู้จักมากขึ้น', '<p>สมัยนี้ อนิเมชั่นหรือการ์ตูนต่างๆเป็นสื่อที่อยู่ใกล้ตัว และเป็นที่รู้จักมากขึ้น ซึ่งบางเรื่องนั้นได้มีการสอดแทรกสาระเนื้อหาที่น่าสนใจ หรือมีการละเล่นต่างๆ ที่หลายๆ คนเคยเล่น แต่ห่างเหินไปเพราะเทคโนโลยีต่างๆ เริ่มมีเกมมาแทนที่ ชุมนุมเราจึงจัดตั้งขึ้นเพื่อวิเคราะห์สาระ แนวคิด และข้อคิด ที่มีอยู่ในอนิเมชันเหล่านั้น รวมไปถึงลักษณะต่างๆ ของอนิเมชั่น และยังนำเกมกระดาน และการละเล่นต่างๆ ที่อยู่ในอนิเมมาฝึกเล่น และเรียนรู้ความแตกต่าง และเพื่อสร้างความสนุกสนาน และความสามัคคีในหมู่คณะอีกด้วย มาสนุกสนานกับอนิเมชัน และเกมกระดานกันเถอะครับ</p>', 'rally_anime.jpg'),
(3, 'ชุมนุม AGAPE\'', 'agape', '\"AGAPE\" นั้นมีหลายความหมาย ความหมายแรก แปลว่า \"อาการอ้าปากค้าง\"', '<p>\r\nAGAPE\' คืออะไร? \"AGAPE\" นั้นมีหลายความหมาย ความหมายแรก แปลว่า \"อาการอ้าปากค้าง\" (ฮามากจนหุบปากไม่ลง 555+) ส่วนอีกความหมายซึ่งเป็นที่นิยมมากกว่า แปลว่า \"ความรักอันบริสุทธิ์จากพระเจ้า\" เป็นความรักโดยแท้จริง เพราะพวกเราชาว \"AGAPE\" จะมอบความรักให้กับน้องๆโดยไม่หวังสิ่งใดตอบแทน :\') Open house นี้ เตรียมตัวไว้ให้ดี เพราะพี่ๆพกความสนุกสนานมาให้น้องๆ ได้เก็บกลับบ้านกันแน่นอน\r\n</p>', 'rally_agape.jpg'),
(4, 'ชุมนุม FOTO+ MAG', 'foto', 'เป็นชุมนุมของผู้ที่สนใจ หรือรักในการถ่ายภาพ ไม่จำกัดว่าสมาชิกจะต้องมีกล้องถ่ายภาพทุกคน ', '<p>\r\nชุมนุมเรา จะเป็นชุมนุมที่รวบรวมคนที่สนใจในด้านการถ่ายภาพ ชุมนุมเราเป็นกลุ่มคนที่ถ่ายภาพบรรยากาศงานให้น้องๆเก็บไว้เป็นที่ระลึก ไม่ว่าจะเป็นงานนิทรรศการ งานวิชาการ งานกีฬา หรืองานพิธีสำคัญ พวกเขามีหน้าที่ในการเก็บภาพบรรยากาศ เหตุการณ์ต่างๆ รอยยิ้ม เสียงหัวเราะ หรือความประทับใจที่เกิดขึ้น ออกมาเป็นภาพถ่าย หรืออาจจะเป็นภาพเคลื่อนไหว\r\n</p>', 'rally_foto.png'),
(5, 'ชุมนุม Inphonic', 'inphonic', 'Inphonic นั้นเป็นการรวมคำว่า \"Information Technology(IT)\" และ \"Symphonic\" เข้าด้วยกัน  ', '<p>น้องๆหลายคนเมื่อได้ยินชื่อชุมนุม Inphonic อาจจะงงกันว่าคือชุมนุมอะไร ซึ่งจริงๆแล้ว Inphonic นั้นเป็นการรวมคำว่า \"Information Technology(IT)\" และ \"Symphonic\" เข้าด้วยกัน หรือพูดง่ายๆก็คือชุมนุมดนตรีของคณะ IT เรานี่เอง ชุมนุม Inphonic นั้นจัดตั้งขึ้นเพื่อเป็นการรวมตัวผู้ชื่นชอบในดนตรีของคณะไอที ซึ่งไม่ว่าใครจะมีฝีมือระดับเทพหรือเพิ่งหัดเล่นก็สามารถเข้าร่วมชุมนุมได้ ขอ เชิญชวนน้องๆผู้ชื่นชอบและหลงไหลในเสียงดนตรีมารับชมรับฟังการแสดงดนตรีจาก พี่ๆไอทีลาดกระบังได้ภายในงาน Open House รับประกันความมันส์ตลอดงาน !!!</p>', 'rally_inphonic.jpg');

