INSERT INTO `workshops` (`id`, `title`, `slug`, `short_description`, `full_description`, `logo_src`) VALUES
(1, 'MULTIMEDIA', 'multimedia', '<strong>สายลับจับผิดภาพ</strong><br/>\r\n<p>เรียนรู้เบื้องหลังการทาเกมจับผิดภาพ</p>', '<h2>เวิร์คชอปด้านสื่อประสม (Multimedia Workshop)</h2>\r\n\r\n<h5>ชื่อกิจกรรม : สายลับจับผิดภาพ</h5>\r\n\r\n<p class=\"indent\">ในเวิร์คชอปนี้ น้อง ๆ จะได้เรียนรู้เบื้องหลังการทาเกมจับผิดภาพ โดยอาศัยหลักการการประมวลผล ภาพเบื้องต้น (Image processing) พร้อมทั้งได้ลงมือเขียนเกมด้วยโปรแกรม Matlab ด้วยตนเอง</p>', 'workshop_multi.svg'),
(2, 'NETWORKS', 'networks', '<strong>IoT Connecting Things : เชื่อมต่อทุกสรรพสิ่งด้วย IoT</strong><br/>\r\n<p>เรียนรู้วิธีการประยุกต์ใช้เทคโนโลยี IoT</p>', '<h2>เวิร์คชอปด้านเครือข่าย (Networks Workshop)</h2>\r\n\r\n<h5>ชื่อกิจกรรม IoT Connecting Things : เชื่อมต่อทุกสรรพสิ่งด้วย IoT</h5>\r\n\r\n<p class=\"indent\">ในเวิร์คชอปนี้ น้อง ๆ จะได้เรียนรู้วิธีการประยุกต์ใช้เทคโนโลยี IoT หรืออินเตอร์เน็ทของสรรพสิ่ง ใน การเชื่อมต่อเข้ากับอินเตอร์เน็ท เพื่อส่งข้อมูลที่ตรวจจับได้จากอุปกรณ์เซ็นเซอร์ ไปเก็บไว้ยังเครื่อง คอมพิวเตอร์บนคราวด์ (Cloud) สาหรับให้น้อง ๆ สามารถเข้ามาตรวจสอบ หรือสามารถแจ้งเตือน ผ่านทาง เครื่องคอมพิวเตอร์ โทรศัพท์มือถือ หรือแทปเล็ทได้ในทุกที่ผ่านทางอินเตอร์เน็ท</p>\r\n<p class=\"indent\">หลังจากจบเวิร์คชอปนี้แล้วน้องๆจะเข้าใจพื้นฐานการทางานของเทคโนโลยีIoT ซึ่งพบเห็นใน ปัจจุบัน เช่น Smart home, Smart farm, Healthcare monitoring เป็นต้น</p>', 'workshop_networks.svg'),
(3, 'SOFTWARE ENGINEERING', 'se', '<strong>Software Defined Robots</strong><br/>\r\n<p>รู้จักโครงสร้างของตัวต่อเลโก้รุ่น Mindstorms NXT</p>', '<h2>เวิร์คชอปด้านวิศวกรรมซอฟต์แวร์ (Software Engineering Workshop)</h2>\r\n\r\n<h5>ชื่อกิจกรรม : Software Defined Robots</h5>\r\n\r\n<p class=\"indent\">ในเวิร์คชอปนี้ น้อง ๆ จะได้รู้จักโครงสร้างของตัวต่อเลโก้ (Lego) รุ่น Mindstorms NXT ซึ่ง ประกอบด้วยเอ็มพียู (MPU) เซ็นเซอร์แสง (Light sensor) เซ็นเซอร์เสียง (Sound sensor) เซ็นเซอร์สัมผัส (Touch sensor) เซ็นเซอร์อัลตราโซนิค (Ultrasonic sensor) และมอร์เตอร์ (Motor) ที่สามารถนามาสร้าง เป็นหุ่นยนต์ในรูปแบบต่าง ๆ ได้ตามที่ต้องการ</p>\r\n<p class=\"indent\">นอกจากนี้ น้อง ๆ ยังจะได้ทดลองเขียนโปรแกรมภาษาจาวา ในการควบคุมสั่งการตัวต่อเลโก้ที่ ประกอบขึ้นเป็นหุ่นยนต์ที่สามารถเคลื่อนที่ได้ เพื่อให้มันสามารถตรวจจับข้อมูลในรูปแบบต่าง ๆ จาก สภาพแวดล้อม แล้วนามาประมวลผล และดาเนินการตามภารกิจที่กาหนด</p>', 'workshop_se.svg'),
(4, 'DATA SCIENCE', 'datascience', '<strong>Data Science finds Pokemon : ตามหาโปเกม่อนด้วยวิทยาการข้อมูล</strong><br/>\r\n<p>เรียนรู้วิธีและลงมือปฏิบัติจริงในการวิเคราะห์ข้อมูลทีละขั้น</p>', '<h2>เวิร์คชอปด้านวิทยาการข้อมูล (Data Science Workshop)</h2>\r\n\r\n<h5>ชื่อกิจกรรม:ตามหาโปเกม่อนด้วยวทิยาการข้อมูล</h5>\r\n\r\n<p class=\"indent\">ในเวิร์คชอปนี้ เราจะพาน้อง ๆ ที่สนใจแหล่งกาเนิดโปเกม่อนในเกมส์ \"โปเกม่อนโก\" ซึ่งน้อง ๆ นักเรียนจะได้เรียนรู้วิธีและลงมือปฏิบัติจริงในการวิเคราะห์ข้อมูลทีละขั้น ทีละตอน โดยใช้เทคนิคด้านการทา เหมืองข้อมูล ซึ่งเป็นศาสตร์ด้านหนึ่งในสาขา วิทยาการข้อมูล เพื่อตามหาแหล่งที่เราจะสามารถตามหาโปเก ม่อนทั้งหมด 14 ชนิด</p>', 'workshop_datascience.svg');

